export type TPopUpButton = {
    text: string;
    fn?: (...args: unknown[]) => void;
};
export type TPopUpKey = string | number;
export type TPopUpOptions = {
    key?: TPopUpKey;
    acceptButton?: TPopUpButton;
    declineButton?: TPopUpButton;
    showAlertButton?: boolean;
    showSuccessButton?: boolean;
};
export type TPopUp = {
    title: string;
    message: string;
    options?: TPopUpOptions;
};
export type TInnerPopUp = {
    title: string;
    message: string;
    options: TPopUpOptions & {
        open: boolean;
        key: TPopUpKey;
    };
};
export type TModalContext = {
    showPopUp: (popUp: TPopUp) => TPopUpKey;
    hidePopUp: (key: TPopUpKey) => void;
};
export type TModalProvider = React.FC<React.PropsWithChildren>;
